import React, { useEffect, useState } from 'react';
import axios from 'axios';
import StudentForm from './components/StudentForm';
import AttendanceTable from './components/AttendanceTable';

const API = 'http://localhost:5000';

function App() {
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState({});

  // ‚úÖ Load Students on Mount
  useEffect(() => {
    axios.get(`${API}/students`)
      .then(res => {
        setStudents(res.data);
        const init = {};
        res.data.forEach(s => (init[s._id] = 'Present'));
        setAttendance(init);
      })
      .catch(() => alert("‚ùå Failed to load students."));
  }, []);

  // ‚úÖ Mark Present/Absent
  const mark = (id, status) => {
    setAttendance(prev => ({ ...prev, [id]: status }));
  };

  // ‚úÖ Save Attendance
  const save = () => {
    const records = Object.entries(attendance).map(([studentId, status]) => ({
      studentId,
      status
    }));

    axios.post(`${API}/attendance`, records )
      .then(() => alert("‚úÖ Attendance saved and SMS sent!"))
      .catch(() => alert("‚ùå Failed to save attendance"));
  };

  // ‚úÖ Download Excel Report
  const downloadExcel = () => {
    window.open(`${API}/export/excel`, '_blank');
  };

  // ‚úÖ Delete Student
  const deleteStudent = async (id) => {
    if (!window.confirm("Are you sure you want to delete this student?")) return;

    const res = await axios.delete(`${API}/students/${id}`)
      .then(() => {
        alert("üóëÔ∏è Student deleted");
        setStudents(prev => prev.filter(s => s._id !== id));
        setAttendance(prev => {
          const updated = { ...prev };
          delete updated[id];
          return updated;
        });
      })
      .catch(() => alert("‚ùå Failed to delete student"));
  };

  return (
    <div className="container">
      <h1>üìò Attendance Tracker</h1>

      <StudentForm refresh={() => window.location.reload()} />

      <AttendanceTable
        students={students}
        attendance={attendance}
        onMark={mark}
        onDelete={deleteStudent}
      />

      <div className="action-buttons">
        <button onClick={save}>‚úÖ Save Attendance</button>
        <button onClick={downloadExcel}>üì• Download Excel</button>
      </div>
    </div>
  );
}

export default App;
