import React, { useEffect, useState } from 'react';
import axios from 'axios';
import StudentForm from './components/StudentForm';
import AttendanceTable from './components/AttendanceTable';

const API = 'http://localhost:5000';

function App() {
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState({});

  useEffect(() => {
    axios.get(`${API}/students`)
      .then(res => {
        setStudents(res.data);
        const init = {};
        res.data.forEach(s => (init[s._id] = 'Present'));
        setAttendance(init);
      })
      .catch(() => alert("Failed to load students."));
  }, []);

  const mark = (id, status) => {
    setAttendance(prev => ({ ...prev, [id]: status }));
  };

  const save = () => {
    const records = Object.entries(attendance).map(([studentId, status]) => ({
      studentId,
      status
    }));

    axios.post(`${API}/attendance`, { records })
      .then(() => alert("âœ… Attendance saved and SMS sent!"))
      .catch(() => alert("âŒ Failed to save attendance"));
  };

  const downloadExcel = () => {
    window.open(`${API}/export/excel`, '_blank');
  };

  const deleteStudent = (id) => {
    if (!window.confirm("Are you sure you want to delete this student?")) return;

    axios.delete(`${API}/students/${id}`)
      .then(() => {
        alert("Student deleted");

        // Remove student from state
        setStudents(prev => prev.filter(s => s._id !== id));
        setAttendance(prev => {
          const updated = { ...prev };
          delete updated[id];
          return updated;
        });
      })
      .catch(() => alert("Failed to delete student"));
  };

  return (
    <div>
      <h1>ðŸ“˜ Attendance Tracker</h1>

      <StudentForm refresh={() => window.location.reload()} />

      <AttendanceTable
        students={students}
        attendance={attendance}
        onMark={mark}
        onDelete={deleteStudent}
      />

      <button onClick={save}>âœ… Save Attendance</button>
      <button onClick={downloadExcel}>ðŸ“¥ Download Excel</button>
    </div>
  );
}

export default App;
