import React from 'react';

function AttendanceTable({ students, attendance, onMark }) {
  return (
    <table border="1">
      <thead>
        <tr><th>Name</th><th>Roll No</th><th>Attendance</th></tr>
      </thead>
      <tbody>
        {students.map(s => (
          <tr key={s._id}>
            <td>{s.name}</td>
            <td>{s.rollNo}</td>
            <td>
              <button onClick={() => onMark(s._id, 'Present')} style={{ background: attendance[s._id] === 'Present' ? 'lightgreen' : '' }}>✅</button>
              <button onClick={() => onMark(s._id, 'Absent')} style={{ background: attendance[s._id] === 'Absent' ? 'salmon' : '' }}>❌</button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
}

export default AttendanceTable;
