import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const API_URL = 'http://localhost:5000';

function App() {
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState({});
  const [newStudent, setNewStudent] = useState({
    name: '',
    rollNo: '',
    parentMobile: ''
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Load students on mount
  useEffect(() => {
    const fetchStudents = async () => {
      try {
        const response = await axios.get(`${API_URL}/students`);
        setStudents(response.data);

        // Initialize all as present
        const initialAttendance = {};
        response.data.forEach(student => {
          initialAttendance[student._id] = 'Present';
        });
        setAttendance(initialAttendance);
      } catch (err) {
        setError('Failed to load students');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchStudents();
  }, []);

  // Handle new student input
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewStudent(prev => ({
      ...prev,
      [name]: value
    }));
  };

  // Add new student
  const addStudent = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`${API_URL}/students`, newStudent);
      setStudents(prev => [...prev, response.data]);

      // Add to attendance with default 'Present'
      setAttendance(prev => ({
        ...prev,
        [response.data._id]: 'Present'
      }));

      // Reset form
      setNewStudent({
        name: '',
        rollNo: '',
        parentMobile: ''
      });
    } catch (err) {
      setError('Failed to add student');
      console.error(err);
    }
  };

  // Toggle attendance status
  const toggleAttendance = (studentId) => {
    setAttendance(prev => ({
      ...prev,
      [studentId]: prev[studentId] === 'Present' ? 'Absent' : 'Present'
    }));
  };

  // Submit attendance
  const submitAttendance = async () => {
    try {
      const records = students.map(student => ({
        studentId: student._id,
        status: attendance[student._id] || 'Present'
      }));

      await axios.post(`${API_URL}/attendance`, { records });
      alert('Attendance submitted successfully!');
    } catch (err) {
      setError('Failed to submit attendance');
      console.error(err);
    }
  };

  // Delete student
  const deleteStudent = async (id) => {
    if (!window.confirm('Are you sure you want to delete this student?')) return;

    try {
      await axios.delete(`${API_URL}/students/${id}`);
      setStudents(prev => prev.filter(student => student._id !== id));

      // Remove from attendance
      setAttendance(prev => {
        const newAttendance = { ...prev };
        delete newAttendance[id];
        return newAttendance;
      });
    } catch (err) {
      setError('Failed to delete student');
      console.error(err);
    }
  };

  // Export to Excel
  const exportToExcel = () => {
    window.open(`${API_URL}/export/excel`, '_blank');
  };

  if (loading) return <div className="loading">Loading...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="app">
      <h1>Student Attendance System</h1>

      {/* Add Student Form */}
      <div className="student-form">
        <h2>Add New Student</h2>
        <form onSubmit={addStudent}>
          <input
            type="text"
            name="name"
            placeholder="Name"
            value={newStudent.name}
            onChange={handleInputChange}
            required
          />
          <input
            type="text"
            name="rollNo"
            placeholder="Roll No"
            value={newStudent.rollNo}
            onChange={handleInputChange}
            required
          />
          <input
            type="tel"
            name="parentMobile"
            placeholder="Parent Mobile (10 digits)"
            value={newStudent.parentMobile}
            onChange={handleInputChange}
            pattern="[0-9]{10}"
            required
          />
          <button type="submit">Add Student</button>
        </form>
      </div>

      {/* Attendance Table */}
      <div className="attendance-table">
        <h2>Today's Attendance</h2>
        <table>
          <thead>
            <tr>
              <th>Roll No</th>
              <th>Name</th>
              <th>Parent Mobile</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {students.map(student => (
              <tr key={student._id}>
                <td>{student.rollNo}</td>
                <td>{student.name}</td>
                <td>{student.parentMobile}</td>
                <td className={attendance[student._id]}>
                  {attendance[student._id] || 'Present'}
                </td>
                <td>
                  <button 
                    onClick={() => toggleAttendance(student._id)}
                    className="toggle-btn"
                  >
                    Toggle
                  </button>
                  <button 
                    onClick={() => deleteStudent(student._id)}
                    className="delete-btn"
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* Action Buttons */}
      <div className="action-buttons">
        <button onClick={submitAttendance} className="submit-btn">
          Submit Attendance
        </button>
        <button onClick={exportToExcel} className="export-btn">
          Export to Excel
        </button>
      </div>
    </div>
  );
}

export default App;
