import React, { useEffect, useState } from 'react';
import axios from 'axios';
import StudentForm from './components/StudentForm';
import AttendanceTable from './components/AttendanceTable';

const API = 'http://localhost:5000';

function App() {
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState({});

  useEffect(() => {
    axios.get(`${API}/students`).then(res => {
      setStudents(res.data);
      const init = {};
      res.data.forEach(s => (init[s._id] = 'Present'));
      setAttendance(init);
    });
  }, []);

  const mark = (id, status) => {
    setAttendance(prev => ({ ...prev, [id]: status }));
  };

  const save = () => {
    const records = Object.entries(attendance).map(([studentId, status]) => ({
      studentId,
      status
    }));
    axios.post(`${API}/attendance/save`, { records })
      .then(() => alert("Attendance saved and SMS sent!"))
      .catch(() => alert("Failed to save attendance"));
  };

  return (
    <div>
      <h1>Attendance Tracker</h1>
      <StudentForm refresh={() => window.location.reload()} />
      <AttendanceTable students={students} attendance={attendance} onMark={mark} />
      <button onClick={save}>Save Attendance</button>
    </div>
  );
}

export default App;
