import React, { useEffect, useState } from 'react';
import axios from 'axios';
import StudentForm from './components/StudentForm';
import AttendanceTable from './components/AttendanceTable';

const API = 'http://localhost:5000';

function App() {
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState({});

  // ✅ Fetch student list and initialize attendance
  useEffect(() => {
    axios.get(`${API}/students`)
      .then(res => {
        setStudents(res.data);
        const init = {};
        res.data.forEach(s => (init[s._id] = 'Present'));
        setAttendance(init);
      })
      .catch(err => {
        console.error("Error loading students:", err.message);
        alert("Failed to load students.");
      });
  }, []);

  // ✅ Mark attendance
  const mark = (id, status) => {
    setAttendance(prev => ({ ...prev, [id]: status }));
  };

  // ✅ Save attendance to backend
  const save = () => {
    const records = Object.entries(attendance).map(([studentId, status]) => ({
      studentId,
      status
    }));

    axios.post(`${API}/attendance`, { records }) // ✅ fixed route
      .then(() => alert("✅ Attendance saved and SMS sent to absentees"))
      .catch((err) => {
        console.error("Save error:", err);
        alert("❌ Failed to save attendance");
      });
  };

  // ✅ Download Excel
  const downloadExcel = () => {
    window.open(`${API}/export/excel`, '_blank');
  };

  return (
    <div>
      <h1>📘 Attendance Tracker</h1>

      <StudentForm refresh={() => window.location.reload()} />

      <AttendanceTable
        students={students}
        attendance={attendance}
        onMark={mark}
      />

      <button onClick={save}>✅ Save Attendance</button>
      <button onClick={downloadExcel}>📥 Download Excel</button>
    </div>
  );
}

export default App;
