import React, { useState } from 'react';
import axios from 'axios';

function StudentForm() {
  const [name, setName] = useState('');
  const [rollNumber, setRollNumber] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    // ✅ Input Validation
    const trimmedName = name.trim();
    const trimmedRoll = rollNumber.trim();

    if (!trimmedName || !trimmedRoll) {
      alert("⚠️ Name and Roll No. cannot be empty.");
      return;
    }

    if (!/^[A-Za-z\s]+$/.test(trimmedName)) {
      alert("⚠️ Name must contain only letters and spaces.");
      return;
    }

    if (!/^[A-Za-z0-9]+$/.test(trimmedRoll)) {
      alert("⚠️ Roll No. must contain only letters and numbers.");
      return;
    }

    try {
      const res = await axios.post('http://localhost:5000/students', {
        name: trimmedName,
        rollNo: trimmedRoll
      });

      console.log("Backend response:", res);

      if (res && res.data) {
        alert("✅ Student added successfully!");
        setName('');
        setRollNumber('');
      } else {
        alert("⚠️ Unexpected response from server.");
      }
    } catch (err) {
      console.error("❌ Error submitting student:", err);
      const errorMsg = err.response?.data?.error || err.message || "Unknown error";
      alert("❌ Failed to add student: " + errorMsg);
    }
  };

  return (
    <div>
      <h2>Add Student</h2>
      <form onSubmit={handleSubmit}>
        <input
          value={name}
          onChange={e => setName(e.target.value)}
          placeholder="Name"
          required
        />
        <input
          value={rollNumber}
          onChange={e => setRollNumber(e.target.value)}
          placeholder="Roll No"
          required
        />
        <button type="submit">Add</button>
      </form>
    </div>
  );
}

export default StudentForm;
